sect1.sect2:
   &reftitle.examples;

    Arayüz örnegi


<?php
// 'iTemplate' arayüzünün tanimlanmasi
interface iTemplate
{
    public function setVariable($name, $var);
    public function getHtml($template);
}

// Arayüzün gerçeklestirilmesi
// Bu çalisacaktir
class Template implements iTemplate
{
    private $vars = array();

    public function setVariable($name, $var)
    {
        $this->vars[$name] = $var;
    }

    public function getHtml($template)
    {
        foreach($this->vars as $name => $value) {
            $template = str_replace('{' . $name . '}', $value, $template);
        }

        return $template;
    }
}

// Bu çalismayacaktir
// Ölümcül hata: BadTemplate sinifi 1 soyut metod içermektedir
// ve bu metodun abstract olarak tanimlanmasi gerekmektedir (iTemplate::getHtml)
class BadTemplate implements iTemplate
{
    private $vars = array();

    public function setVariable($name, $var)
    {
        $this->vars[$name] = $var;
    }
}

?>




   Ayrica bakin: instanceof operatörü.
