sect1.para:
   Bu kuralları bir örnek ile netleştirelim.

     PHP 5 içerisinde nesne karşılaştırma örneği


<?php
function bool2str($bool)
{
    if ($bool === false) {
        return 'FALSE';
    } else {
        return 'TRUE';
    }
}

function compareObjects(&$o1, &$o2)
{
    echo 'o1 == o2 : ' . bool2str($o1 == $o2) . "\n";
    echo 'o1 != o2 : ' . bool2str($o1 != $o2) . "\n";
    echo 'o1 === o2 : ' . bool2str($o1 === $o2) . "\n";
    echo 'o1 !== o2 : ' . bool2str($o1 !== $o2) . "\n";
}

class Flag
{
    public $flag;

    function Flag($flag = true) {
        $this->flag = $flag;
    }
}

class OtherFlag
{
    public $flag;

    function OtherFlag($flag = true) {
        $this->flag = $flag;
    }
}

$o = new Flag();
$p = new Flag();
$q = $o;
$r = new OtherFlag();

echo "Aynı sınıfa ait iki örnek\n";
compareObjects($o, $p);

echo "\nAynı örneğe ait iki referans\n";
compareObjects($o, $q);

echo "\nİki farklı sınıftan oluşturulmuş örnek\n";
compareObjects($o, $r);
?>


     &example.outputs;


Aynı sınıfa ait iki örnek
o1 == o2 : TRUE
o1 != o2 : FALSE
o1 === o2 : FALSE
o1 !== o2 : TRUE

Aynı örneğe ait iki referans
o1 == o2 : TRUE
o1 != o2 : FALSE
o1 === o2 : TRUE
o1 !== o2 : FALSE

İki farklı sınıftan oluşturulmuş örnek
o1 == o2 : FALSE
o1 != o2 : TRUE
o1 === o2 : FALSE
o1 !== o2 : TRUE
