   Bu kurallarý bir örnek ile netleþtirelim.

     PHP 5 içerisinde nesne karþýlaþtýrma örneði


<?php
function bool2str($bool)
{
    if ($bool === false) {
        return 'FALSE';
    } else {
        return 'TRUE';
    }
}

function compareObjects(&$o1, &$o2)
{
    echo 'o1 == o2 : ' . bool2str($o1 == $o2) . "\n";
    echo 'o1 != o2 : ' . bool2str($o1 != $o2) . "\n";
    echo 'o1 === o2 : ' . bool2str($o1 === $o2) . "\n";
    echo 'o1 !== o2 : ' . bool2str($o1 !== $o2) . "\n";
}

class Flag
{
    public $flag;

    function Flag($flag = true) {
        $this->flag = $flag;
    }
}

class OtherFlag
{
    public $flag;

    function OtherFlag($flag = true) {
        $this->flag = $flag;
    }
}

$o = new Flag();
$p = new Flag();
$q = $o;
$r = new OtherFlag();

echo "Ayný sýnýfa ait iki örnek\n";
compareObjects($o, $p);

echo "\nAyný örneðe ait iki referans\n";
compareObjects($o, $q);

echo "\nÝki farklý sýnýftan oluþturulmuþ örnek\n";
compareObjects($o, $r);
?>


     &example.outputs;


Ayný sýnýfa ait iki örnek
o1 == o2 : TRUE
o1 != o2 : FALSE
o1 === o2 : FALSE
o1 !== o2 : TRUE

Ayný örneðe ait iki referans
o1 == o2 : TRUE
o1 != o2 : FALSE
o1 === o2 : TRUE
o1 !== o2 : FALSE

Ýki farklý sýnýftan oluþturulmuþ örnek
o1 == o2 : FALSE
o1 != o2 : TRUE
o1 === o2 : FALSE
o1 !== o2 : TRUE
