  ["n"]=>
  int(1)
  ["x:private"]=>
  array(2) {
    ["b"]=>
    int(2)
    ["c"]=>
    int(3)
  }
}

    __call ile overload örnegi


<?php
class Caller
{
    private $x = array(1, 2, 3);

    private function __call($m, $a)
    {
        print "$m metodu çagrildi:\n";
        var_dump($a);
        return $this->x;
    }
}

$foo = new Caller();
$a = $foo->test(1, "2", 3.4, true);
var_dump($a);
?>


    &example.outputs;



