refentry.refsect1:
  &reftitle.examples;



    bzread örneği


<?php

$dosya = '/tmp/foo.bz2';
$bz = bzopen($dosya, 'r') or die("Belirtilen dosya $dosya açılamadı.");

$acilmis_icerik = '';
while (!feof($bz)) {
  $acilmis_icerik .= bzread($bz, 4096);
}
bzclose($bz);

echo "Belirtilen dosya $dosya içeriği: <br />\n";
echo $acilmis_icerik;

?>
